CREATE TABLE `registrations` (`id` INT(11) NOT NULL AUTO_INCREMENT, `Name` VARCHAR(255)  NOT NULL, `Pin` INT(10) NOT NULL, `Year` TINYINT(1) NOT NULL, `Email` VARCHAR(255)  NOT NULL, `Phone` BIGINT(10) NOT NULL, `Gender` VARCHAR(6)  NOT NULL, `Registered on` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, `Password` VARCHAR(255)  NOT NULL DEFAULT '8938e22ed7c20cfd3ce205673b421792', PRIMARY KEY (`id`)) ENGINE = InnoDB;